{
  "recipes": [
    {
        "name": "Bandeja Paisa",
        "author": "Doña Gloria",
        "image_link": "https://cdn.pixabay.com/photo/2020/06/20/17/44/food-5321841_1280.jpg",
        "recipe": [
            "Cocine los frijoles con hogao y plátano maduro.",
            "Prepare el arroz blanco, carne molida y chicharrón.",
            "Fría los huevos y sirva con aguacate.",
            "Acompañe con una arepa antioqueña."
        ]
    },
    {
        "name": "Ajiaco Santafereño",
        "author": "Chef Andrés",
        "image_link": "https://cdn.pixabay.com/photo/2019/09/25/16/29/tamale-4504060_960_720.jpg",
        "recipe": [
            "Cocine las papas con pollo y mazorca.",
            "Agregue guascas y deje cocinar a fuego lento.",
            "Sirva con crema de leche y alcaparras.",
            "Acompañe con arroz blanco y aguacate."
        ]
    },
    {
        "name": "Sancocho Valluno",
        "author": "Abuela Marta",
        "image_link": "https://cdn.pixabay.com/photo/2016/07/20/07/00/gastronomy-1529736_1280.jpg",
        "recipe": [
            "Cocine el pollo en agua con cebolla y ajo.",
            "Agregue yuca, papa criolla y plátano verde.",
            "Incorpore mazorca y cilantro fresco.",
            "Sirva con arroz blanco y aguacate."
        ]
    },
    {
        "name": "Arepas de Chócolo",
        "author": "Panadería El Molino",
        "image_link": "https://cdn.pixabay.com/photo/2014/09/23/04/19/corn-457153_960_720.jpg",
        "recipe": [
            "Licúe maíz tierno con leche, azúcar y sal.",
            "Vierta la mezcla en un sartén caliente con mantequilla.",
            "Cocine hasta dorar por ambos lados.",
            "Acompañe con queso fresco derretido."
        ]
    },
    {
        "name": "Empanadas Colombianas",
        "author": "Doña Luz",
        "image_link": "https://cdn.pixabay.com/photo/2020/02/11/19/03/meal-4840666_1280.jpg",
        "recipe": [
            "Prepare la masa con harina de maíz, agua y sal.",
            "Rellene con carne desmechada y papa criolla.",
            "Forme las empanadas y séllalas con cuidado.",
            "Fríalas en aceite caliente hasta dorar."
        ]
    }
  ]
}
